// Filter: Only pass through if this is a callback query (not a regular message)
const data = $json.body || $json;
if (data.callback_query) {
  return $input.all();
}
return [];

// Filter: Only pass through if this is a regular message (not a callback query)
const data = $json.body || $json;
if (data.message && !data.callback_query) {
  return $input.all();
}
return [];

// Parse callback_data format: action_bidId_reviewType or action|bidId|reviewType
// Handle both direct data and webhook body format
const data = $json.body || $json;
const callbackQuery = data.callback_query;
const callbackData = callbackQuery.data;
// Support both _ and | as delimiters
const delimiter = callbackData.includes('|') ? '|' : '_';
const parts = callbackData.split(delimiter);

if (parts.length < 3) {
  throw new Error(`Invalid callback_data format: ${callbackData}`);
}

const action = parts[0]; // approve, revision, reject
const bidId = parts[1]; // UUID
const reviewType = parts[2]; // tech, comm, mgmt

// Map short codes to full review types
const reviewTypeMap = {
  'tech': 'TECHNICAL',
  'comm': 'COMMERCIAL',
  'mgmt': 'MANAGEMENT'
};

const fullReviewType = reviewTypeMap[reviewType];
if (!fullReviewType) {
  throw new Error(`Invalid review type: ${reviewType}`);
}

// Map actions to decisions
const decisionMap = {
  'approve': 'APPROVED',
  'revision': 'REVISION_REQUESTED',
  'reject': 'REJECTED'
};

const decision = decisionMap[action];
if (!decision) {
  throw new Error(`Invalid action: ${action}`);
}

return [{
  json: {
    callback_id: callbackQuery.id,
    action: action,
    decision: decision,
    bid_id: bidId,
    review_type: fullReviewType,
    user_id: callbackQuery.from.id,
    username: callbackQuery.from.username,
    first_name: callbackQuery.from.first_name,
    message_id: callbackQuery.message.message_id,
    chat_id: callbackQuery.message.chat.id,
    needs_reason: action !== 'approve' // Revision and reject need reasons
  }
}];

// Route based on action - for approvals, pass data through; for others, stop flow here
const action = $('Parse Callback Data').first().json.action;
if (action === 'approve') {
  // Approval - return empty to skip needs-reason branch, continue to update decision
  return [];
}
// Needs reason - pass all data through
return $input.all();

// Route based on action - for non-approvals (revision/reject), pass data through
const action = $('Parse Callback Data').first().json.action;
if (action !== 'approve') {
  // Needs reason - return empty to skip direct update, continue to needs-reason flow
  return [];
}
// Approval - pass all data through
return $input.all();

