// Filter: Only pass through if review_type is TECHNICAL
const reviewType = $('Parse Callback Data').first().json.review_type;
if (reviewType === 'TECHNICAL') {
  return $input.all();
}
return [];

// Filter: Only pass through if review_type is COMMERCIAL
const reviewType = $('Parse Callback Data').first().json.review_type;
if (reviewType === 'COMMERCIAL') {
  return $input.all();
}
return [];

// Filter: Only pass through if review_type is MANAGEMENT
const reviewType = $('Parse Callback Data').first().json.review_type;
if (reviewType === 'MANAGEMENT') {
  return $input.all();
}
return [];

// Filter: Only pass through if state_type exists (has pending state)
if ($json.state_type) {
  return $input.all();
}
return [];

// Process the reason message with stored context
// Handle both direct data and webhook body format
const webhookData = $('Webhook: Telegram Callback').first().json;
const data = webhookData.body || webhookData;
const message = data.message;

const state = $('Check Conversation State').first().json;
// context_json might be a string or already parsed object
const context = typeof state.context_json === 'string' ? JSON.parse(state.context_json) : state.context_json;
const reason = message.text;

return [{
  json: {
    state_type: state.state_type,
    reason: reason.trim(),
    bid_id: context.bid_id,
    review_type: context.review_type,
    decision: context.decision,
    message_id: context.message_id,
    reviewer_id: context.reviewer_id,
    reviewer_name: context.reviewer_name,
    chat_id: message.chat.id,
    user_id: message.from.id
  }
}];

