parameters.jsCode:
// Validate incoming bid submission - returns error object instead of throwing
const body = $input.first().json.body;
const errors = [];

// Required fields validation
const requiredFields = ['title', 'client_name', 'submission_deadline'];
for (const field of requiredFields) {
  if (!body[field] || body[field].toString().trim() === '') {
    errors.push(`Missing required field: ${field}`);
  }
}

// Validate deadline format and value
let deadline = null;
if (body.submission_deadline) {
  deadline = new Date(body.submission_deadline);
  if (isNaN(deadline.getTime())) {
    errors.push('Invalid submission_deadline format');
  } else if (deadline < new Date()) {
    errors.push('submission_deadline must be in the future');
  }
}

// Validate estimated_value if provided
if (body.estimated_value !== undefined) {
  const value = parseFloat(body.estimated_value);
  if (isNaN(value) || value < 0) {
    errors.push('estimated_value must be a positive number');
  }
}

// If validation failed, return error object (don't throw)
if (errors.length > 0) {
  return [{ json: { validated: false, error: { message: errors.join('; ') } } }];
}

// Build validated bid object
const validatedBid = {
  title: body.title.trim(),
  client_name: body.client_name.trim(),
  client_contact: body.client_contact?.trim() || null,
  client_email: body.client_email?.trim() || null,
  estimated_value: body.estimated_value ? parseFloat(body.estimated_value) : null,
  currency: body.currency?.toUpperCase() || 'USD',
  margin_percentage: body.margin_percentage ? parseFloat(body.margin_percentage) : null,
  submission_deadline: deadline.toISOString(),
  decision_expected_date: body.decision_expected_date ? new Date(body.decision_expected_date).toISOString() : null,
  project_start_date: body.project_start_date ? new Date(body.project_start_date).toISOString() : null,
  project_duration_days: body.project_duration_days ? parseInt(body.project_duration_days) : null,
  priority: ['LOW', 'MEDIUM', 'HIGH', 'CRITICAL'].includes(body.priority?.toUpperCase()) ? body.priority.toUpperCase() : 'MEDIUM',
  document_urls: Array.isArray(body.document_urls) ? body.document_urls : (body.document_urls ? [body.document_urls] : []),
  source: body.source || 'webhook',
  tags: Array.isArray(body.tags) ? body.tags : [],
  notes: body.notes?.trim() || null,
  submitted_by: body.submitted_by || null
};

return [{ json: { validated: true, bid: validatedBid } }];

