// Normalize reviewer result - ensure we always have an item to process
// even when the database returns 0 rows
const items = $input.all();
if (items.length === 0 || !items[0].json.id) {
  // No reviewer found - create a null marker
  return [{ json: { id: null, no_reviewer: true } }];
}
// Reviewer found - pass through
return items;

// Code filter: Reviewer Found - continue if reviewer exists
const reviewer = $input.first().json;
if (reviewer && reviewer.id && !reviewer.no_reviewer) {
  return $input.all();
}
return [];

// Code filter: No Reviewer - continue if no reviewer exists
const reviewer = $input.first().json;
if (!reviewer || !reviewer.id || reviewer.no_reviewer) {
  return $input.all();
}
return [];
