// Build daily summary message
const summary = $('Get Active Bids Summary').first().json;
const deadlines = $('Get Upcoming Deadlines').all();
const breaches = $('Get SLA Breaches').all();

const today = new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

let message = `üìä DAILY BIDDING REPORT\n${today}\n\n`;

// Summary section
message += `üìã PIPELINE SUMMARY:\n`;
message += `‚Ä¢ Total Active: ${summary.total_active || 0}\n`;
message += `‚Ä¢ In Review: ${(summary.tech_review || 0) + (summary.comm_review || 0) + (summary.mgmt_approval || 0)}\n`;
message += `‚Ä¢ Needs Info: ${summary.needs_info || 0}\n`;
message += `‚Ä¢ Ready to Submit: ${summary.approved || 0}\n`;
message += `‚Ä¢ Total Pipeline Value: $${summary.total_value ? Number(summary.total_value).toLocaleString() : '0'}\n`;
message += `‚Ä¢ Avg Win Probability: ${summary.avg_win_prob ? Math.round(summary.avg_win_prob) : 'N/A'}%\n\n`;

// Deadline warnings
if (deadlines.length > 0) {
  message += `‚ö†Ô∏è UPCOMING DEADLINES (3 days):\n`;
  for (const d of deadlines.slice(0, 5)) {
    const daysText = d.json.days_remaining < 1 ? 'TODAY!' : `${Math.ceil(d.json.days_remaining)} days`;
    message += `‚Ä¢ ${d.json.reference_number}: ${daysText} - ${d.json.client_name}\n`;
  }
  message += `\n`;
}

// SLA breaches
if (breaches.length > 0) {
  message += `üö® SLA BREACHES (${breaches.length}):\n`;
  for (const b of breaches.slice(0, 5)) {
    message += `‚Ä¢ ${b.json.reference_number} (${b.json.review_type}) - ${Math.round(b.json.hours_overdue)}h overdue - ${b.json.reviewer_name}\n`;
  }
  message += `\n`;
}

// Status counts
message += `üìà BY REVIEW STAGE:\n`;
message += `‚Ä¢ Technical: ${summary.tech_review || 0}\n`;
message += `‚Ä¢ Commercial: ${summary.comm_review || 0}\n`;
message += `‚Ä¢ Management: ${summary.mgmt_approval || 0}\n`;

return [{ json: { message: message, has_breaches: breaches.length > 0, breach_count: breaches.length } }];

