// Build weekly analytics report
const stats = $('Get Weekly Stats').first().json;
const reviewers = $('Get Reviewer Performance').all();

const weekStart = new Date();
weekStart.setDate(weekStart.getDate() - 7);
const weekEnd = new Date();

let message = `üìà WEEKLY BIDDING ANALYTICS\n`;
message += `${weekStart.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} - ${weekEnd.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}\n\n`;

// Win/Loss summary
message += `üèÜ OUTCOMES THIS WEEK:\n`;
message += `‚Ä¢ Won: ${stats.won || 0} ($${stats.won_value ? Number(stats.won_value).toLocaleString() : '0'})\n`;
message += `‚Ä¢ Lost: ${stats.lost || 0} ($${stats.lost_value ? Number(stats.lost_value).toLocaleString() : '0'})\n`;
message += `‚Ä¢ No Decision: ${stats.no_decision || 0}\n`;
message += `‚Ä¢ WIN RATE: ${stats.win_rate || 0}%\n\n`;

// Activity
message += `üìä ACTIVITY:\n`;
message += `‚Ä¢ New Submissions: ${stats.new_submissions || 0}\n`;
message += `‚Ä¢ Decisions Made: ${stats.total_decided || 0}\n\n`;

// Reviewer leaderboard
if (reviewers.length > 0) {
  message += `üë• REVIEWER PERFORMANCE:\n`;
  for (let i = 0; i < Math.min(reviewers.length, 5); i++) {
    const r = reviewers[i].json;
    const medal = i === 0 ? 'ü•á' : i === 1 ? 'ü•à' : i === 2 ? 'ü•â' : '‚Ä¢';
    message += `${medal} ${r.name}: ${r.reviews_completed} reviews (${r.avg_response_hours || 'N/A'}h avg)\n`;
  }
}

return [{ json: { message: message } }];
